(284, 'a48395be08ace700d6da211fa7ca13fa', 'КАКИЕ СУЩЕСТВУЮТ ЭТАПЫ ПРИ СОЗДАНИИ САЙТА?\r\nАНАЛИЗ КОНКУРИРУЮЩИХ САЙТОВ, СОСТАВЛЕНИЕ ТЕХНИЧЕСКОГО ЗАДАНИЯ, РАЗРАБОТКА МАКЕТА РЕСУРСА ДЛЯ ГЛАВНОЙ СТРАНИЦЫ, КОРРЕКТИРОВКА, ВЁРСТКА, ПРОГРАММИРОВАНИЕ\r\n'),
(285, '46ad2565c1bf7fc7f57ab7d65fc760d7', 'СКОЛЬКО ТРЕБУЕТСЯ ВРЕМЕНИ ДЛЯ СОЗДАНИЯ САЙТА?\r\nМИНИМУМ – 10 ДНЕЙ, МАКСИМУМ – 5 МЕСЯЦЕВ.\r\nВСЕ ЗАВИСИТ ОТ КОЛИЧЕСТВА ТРЕБУЕМЫХ ФУНКЦИЙ И СЛОЖНОСТИ ОФОРМЛЕНИЯ ДИЗАЙНА\r\n'),
(286, '44b907cacaedca34791007835466aed1', 'ГДЕ ЗАКАЗАТЬ ВНЕДРЕНИЕ БИТРИКС24?\r\nСПЕЦИАЛИСТЫ КОМПАНИИ CODEX РЕШАЮТ ЛЮБЫЕ ЗАДАЧИ, НЕЗАВИСИМО ОТ СЛОЖНОСТИ\r\n'),
(287, 'ef066787b0eed38349a63344b36b22bd', 'ВХОДИТ ЛИ В СТОИМОСТЬ СОЗДАНИЯ САЙТА ЕГО НАПОЛНЕНИЕ КОНТЕНТОМ?\r\nВСЕ ЗАВИСИТ ОТ ТРЕБОВАНИЙ ЗАКАЗЧИКА. НАПОЛНЕНИЕ ИНФОРМАЦИЕЙ САЙТА В ОБЩУЮ СТОИМОСТЬ НЕ ВХОДИТ\r\n'),
(288, '35085520cbaa03ed9cc5155198adcfe7', 'ЧТО ОЗНАЧАЕТ АУТСТАФФИНГ IT-СПЕЦИАЛИСТОВ?\r\nЭТО НАЁМ КВАЛИФИЦИРОВАННЫХ СПЕЦИАЛИСТОВ ДЛЯ РАБОТЫ В СФЕРЕ ИНФОРМАЦИОННЫХ ТЕХНОЛОГИЙ\r\n'),
(289, '7642f4b3a48c8910bbaa2a81284932d7', 'ЧТО ДЕЛАТЬ В СЛУЧАЕ НЕСООТВЕТСТВИЯ РЕЗУЛЬТАТА МОИМ ОЖИДАНИЯМ?\r\nПОСКОЛЬКУ УНИВЕРСАЛЬНЫХ РЕШЕНИЙ НЕ СУЩЕСТВУЕТ, ТО МЫ ОРИЕНТИРУЕМСЯ С УЧЕТОМ ТРЕБОВАНИЙ ЗАКАЗЧИКА. ЭТО ЗНАЧИТ, ЧТО РАЗРАБОТАННЫЙ НАМИ САЙТ ВСЕГДА СООТВЕТСТВУЕТ ПРЕДОСТАВЛЕННЫМ ДАННЫМ. ВНЕШНИЙ ВИД И СТРУКТУРА РЕСУРСА СОГЛАСОВЫВАЮТСЯ С ЗАКАЗЧИКОМ НА ЭТАПЕ ПОДГОТОВКИ МАКЕТА ПРОЕКТА\r\n'),
(290, '9e67077075779adbfc6e5e875726c52c', 'ЧТО ТАКОЕ ПОДДЕРЖКА САЙТА?\r\nУСЛУГА ПОДРАЗУМЕВАЕТ НАПОЛНЕНИЕ РЕСУРСА КОНТЕНТОМ, ЕГО ОБРАБОТКУ, ПЕРИОДИЧЕСКУЮ КОРРЕКТИРОВКУ САЙТА, ПРОДВИЖЕНИЕ В ПОИСКОВЫХ СИСТЕМАХ, ИНФОРМАЦИОННУЮ ЗАЩИТУ ОТ ХАКЕРСКИХ АТАК И ПРОНИКНОВЕНИЯ ВРЕДОНОСНЫХ ПРОГРАММ\r\n'),
